import React, { useState } from 'react'
import "./App.css"

export default function App() {

  const Data = [
    {
      id: 1,
      makale: "hobbala vede zubbala"
    },
    {
      id: 2,
      makale: "zınk zınk zınk hooo bale"
    },
    {
      id: 3,
      makale: "hobbala vede zubba la"
    }
  ]

  const [data, setData] = useState("null");


  function getData(value) {
    setData(value.target.value)
  }

  function Hesapla(value) {

    const makaleTest = [];

    data.forEach(element => {
      makaleTest.push(element.value)
    });

    console.warn(makaleTest)
  }

  return (
    <div>
      <br />
      <br />
      {Data.map((item) => (
        <p style={{ marginLeft: "10vw", marginTop: "0.5vw" }} >{item.makale}</p>
      ))}
      <input type="text" name="makale" onChange={getData} style={{ marginLeft: "10vw", marginTop: "1vw", width: "50vw" }} />
      <button onClick={Hesapla}>Kontrol et</button>
      <p style={{ marginLeft: "10vw", marginTop: "5vw" }} >{data}</p>
    </div>
  )
}
